package com.example.lesson1kotlin2.fragments

import android.os.Bundle
import androidx.fragment.app.Fragment
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import com.example.lesson1kotlin2.databinding.FragmentThirdBinding


class ThirdFragment : Fragment() {

    private var _binding: FragmentThirdBinding? = null
    private val binding: FragmentThirdBinding get() = _binding!!

    private val adapter = CriptoAdapter()


    override fun onCreateView(
        inflater: LayoutInflater, container: ViewGroup?,
        savedInstanceState: Bundle?
    ): View {
        _binding = FragmentThirdBinding.inflate(inflater, container, false)
        return binding.root
    }

    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
        super.onViewCreated(view, savedInstanceState)
        connectRecyclerView()
    }

    private fun connectRecyclerView() {
        binding.thirdRecyclerView.adapter = this.adapter
        this.adapter.setCryptoList(
            mutableListOf(
                Cryptocurrency(
                    "https://happycoin.club/wp-content/uploads/2021/10/litecoin.jpg",
                    "Litecoin пиринговая электронная платёжная система, использующая одноимённую криптовалюту. Litecoin является вторым после Namecoin форком Bitcoin и имеет лишь небольшие отличия от него."
                ),

                Cryptocurrency(
                    "data:image/jpeg;base64,",
                    "Shiba Inu SHIB — децентрализованная криптовалюта, разработанная в августе 2020 года анонимом либо группой лиц, известной как «Риоши». Криптовалюта названа в честь шиба-ину (柴犬), японской породы собак, происходящей из региона Тюбу. Это та же самая порода, которая символизирует криптовалюту Dogecoin, основанную на меме Доге. Шиба-ину характеризуют как «мем-коин». Существуют опасения по поводу концентрации монеты вокруг одного кошелька, контролирующего токены на миллиарды долларов, а также бешеных покупок со стороны розничных инвесторов, мотивированных страхом упустить выгоду.",
                ),
                Cryptocurrency(
                    "https://bits.media/upload/iblock/7eb/5uv3logodmxgozmoxtse4q3o2zsl5j1w/nestabilnyy_steyblkoin_kak_na_samom_dele_tether_obespechivaet_kriptovalyutu.png",
                    "Tether (₮) — криптовалютный токен, выпущенный компанией Tether Limited, которая утверждает, что его стоимость на 80 процентов обеспечивается запасами долларов США на банковских счетах или их эквивалент. Основная идея разработчиков данного токена состоит в предоставлении участникам криптовалютного рынка возможности пользоваться стабильным цифровым активом («стейблкойном»), курс которого привязан к курсу доллара США и не испытывает столь сильных колебаний, как курсы других криптовалют[2]. Tether выпущен на платформе Omni Layer, являющейся надстройкой над блокчейном биткойна"
                )
            )
        )
    }

    override fun onDestroyView() {
        super.onDestroyView()
        _binding = null
    }
}